INSERT INTO `item` VALUES (10, '黔菜中最有名气的传统菜之\r\n一。为贵州独创的酱辣味菜，\r\n具有红而不辣、辣而不猛、辣香味浓、油而不腻和鲜香嫩脆\r\n的特点。 ', '清炒土豆片', 'upload/20170310153856994848.jpg', 150);
INSERT INTO `item` VALUES (13, '清汤活海参选用山东胶东半岛活海参，海参俗称海中人参，用雅轩自制的清鸡汤制作而成，成品口感脆爽，汤清味醇，长期食用可延年益寿。', '麻辣火锅', 'upload/20170310154528463421.jpg', 160);
